'use strict';

var wallets = require('@thirdweb-dev/wallets');

const bloctoWallet = options => ({
  id: wallets.BloctoWallet.id,
  meta: wallets.BloctoWallet.meta,
  create(walletOptions) {
    return new wallets.BloctoWallet({
      ...walletOptions,
      ...options
    });
  },
  isInstalled() {
    return false;
  }
});

exports.bloctoWallet = bloctoWallet;
